sprites.onOverlap(SpriteKind.Player, SpriteKind.PileSand, function (sprite, otherSprite) {
    timer.after(10, function () {
        myEffect = extraEffects.createCustomSpreadEffectData(
        [5, 4],
        true,
        extraEffects.createPresetSizeTable(ExtraEffectPresetShape.Cloud),
        extraEffects.createPercentageRange(50, 100),
        extraEffects.createPercentageRange(50, 100),
        extraEffects.createTimeRange(200, 400),
        0,
        0,
        extraEffects.createPercentageRange(50, 100),
        2,
        0,
        100
        )
        mySprite.fx += 900
        extraEffects.createSpreadEffectOnAnchor(otherSprite, extraEffects.createSingleColorSpreadEffectData(5, ExtraEffectPresetShape.Explosion), 500, 48, 20)
        extraEffects.createSpreadEffectOnAnchor(sprite, extraEffects.createSingleColorSpreadEffectData(5, ExtraEffectPresetShape.Explosion), 1000, 48, 50)
        timer.after(100, function () {
            sprites.destroy(otherSprite)
        })
    })
})
sprites.onOverlap(SpriteKind.Player, SpriteKind.Enemy, function (sprite, otherSprite) {
    if (mySprite.y <= otherSprite.y) {
        if (!(characterAnimations.matchesRule(mySprite, characterAnimations.rule(Predicate.HittingWallDown)))) {
            sprites.destroy(otherSprite)
            music.play(music.createSoundEffect(
            WaveShape.Noise,
            3900,
            3500,
            3000,
            31,
            100,
            SoundExpressionEffect.None,
            InterpolationCurve.Linear
            ), music.PlaybackMode.InBackground)
            music.play(music.melodyPlayable(music.bigCrash), music.PlaybackMode.InBackground)
            mySprite.ay = -20000
            statusbar.value += 20
        } else if (KickTime == true) {
            sprites.destroy(otherSprite)
            mySprite.ay = -20000
            music.play(music.createSoundEffect(
            WaveShape.Noise,
            3900,
            3500,
            3000,
            31,
            100,
            SoundExpressionEffect.None,
            InterpolationCurve.Linear
            ), music.PlaybackMode.InBackground)
            music.play(music.melodyPlayable(music.bigCrash), music.PlaybackMode.InBackground)
            statusbar.value += 20
        } else {
            if (INVISAFrames == false) {
                if (characterAnimations.matchesRule(mySprite, characterAnimations.rule(Predicate.FacingRight)) || characterAnimations.matchesRule(mySprite, characterAnimations.rule(Predicate.MovingRight))) {
                    if (INVISAFrames == false) {
                        mySprite.vx = -200
                    }
                } else if (characterAnimations.matchesRule(mySprite, characterAnimations.rule(Predicate.FacingLeft)) || characterAnimations.matchesRule(mySprite, characterAnimations.rule(Predicate.MovingLeft))) {
                    if (INVISAFrames == false) {
                        mySprite.vx = 200
                    }
                }
                INVISAFrames = true
                info.changeLifeBy(-1)
                info.changeScoreBy(-3)
                timer.after(1000, function () {
                    INVISAFrames = false
                })
            }
        }
    } else if (KickTime == true) {
        sprites.destroy(otherSprite)
        mySprite.ay = -20000
        statusbar.value += 20
    } else if (mySprite.y > otherSprite.y) {
        if (INVISAFrames == false) {
            INVISAFrames = true
            info.changeLifeBy(-1)
            info.changeScoreBy(-3)
            timer.after(1000, function () {
                INVISAFrames = false
            })
        }
    }
})
sprites.onOverlap(SpriteKind.Player, SpriteKind.S2, function (sprite, otherSprite) {
    if (characterAnimations.matchesRule(sprite, characterAnimations.rule(Predicate.NotMoving))) {
        sprite.x += 0
        sprite.y += 0
    }
    sprite.ay = 0
    controller.moveSprite(sprite, 0, 0)
    if (sprite.y != 0) {
        if (controller.A.isPressed()) {
            sprite.ay = 500
            timer.after(500, function () {
                sprite.ay = 0
                sprite.vy = 0
            })
        } else {
            sprite.ay = 0
            sprite.vy = 0
        }
    }
    if (controller.left.isPressed() || characterAnimations.matchesRule(mySprite, characterAnimations.rule(Predicate.MovingLeft))) {
        sprite.x += -4
        sprite.y += -9
        if (controller.A.isPressed()) {
            spriteutils.jumpImpulse(mySprite, 60)
        }
    }
    if (controller.right.isPressed() || characterAnimations.matchesRule(mySprite, characterAnimations.rule(Predicate.MovingRight))) {
        sprite.x += 5
        sprite.y += 4
    }
    if (controller.B.isPressed() && characterAnimations.matchesRule(mySprite, characterAnimations.rule(Predicate.MovingLeft))) {
        sprite.x += -4
        sprite.y += -9
    }
    if (controller.B.isPressed() && characterAnimations.matchesRule(mySprite, characterAnimations.rule(Predicate.MovingRight))) {
        sprite.x += 5
        sprite.y += 4
    }
    if (controller.A.isPressed()) {
        spriteutils.jumpImpulse(mySprite, 60)
    }
})
controller.player2.onButtonEvent(ControllerButton.B, ControllerButtonEvent.Pressed, function () {
    if (GameStart == true) {
        if (Gravity == true) {
            if (ControlsType == 1) {
                if (CoolDown == false) {
                    statusbar.setColor(5, 8)
                    KickTime = true
                    CoolDown = true
                    mySprite.ay = -9000
                    animation.runImageAnimation(
                    mySprite,
                    assets.animation`D-Kick`,
                    50,
                    true
                    )
                    characterAnimations.setCharacterAnimationsEnabled(mySprite, false)
                    timer.after(500, function () {
                        characterAnimations.setCharacterAnimationsEnabled(mySprite, true)
                        animation.stopAnimation(animation.AnimationTypes.All, mySprite)
                        statusbar.setColor(10, 8)
                        KickTime = false
                    })
                    timer.after(1000, function () {
                        CoolDown = false
                    })
                }
            }
        }
    }
})
