sprites.onOverlap(SpriteKind.Player, SpriteKind.EnemyT2, function (sprite, otherSprite) {
    if (mySprite.y <= otherSprite.y) {
        if (!(characterAnimations.matchesRule(mySprite, characterAnimations.rule(Predicate.HittingWallDown)))) {
            sprites.destroy(otherSprite)
            mySprite.ay = -20000
            statusbar.value += 20
        } else if (KickTime == true) {
            sprites.destroy(otherSprite)
            mySprite.ay = -20000
            statusbar.value += 20
        } else {
            if (INVISAFrames == false) {
                if (characterAnimations.matchesRule(mySprite, characterAnimations.rule(Predicate.FacingRight)) || characterAnimations.matchesRule(mySprite, characterAnimations.rule(Predicate.MovingRight))) {
                    if (INVISAFrames == false) {
                        mySprite.vx = -200
                    }
                } else if (characterAnimations.matchesRule(mySprite, characterAnimations.rule(Predicate.FacingLeft)) || characterAnimations.matchesRule(mySprite, characterAnimations.rule(Predicate.MovingLeft))) {
                    if (INVISAFrames == false) {
                        mySprite.vx = 200
                    }
                }
                INVISAFrames = true
                info.changeLifeBy(-1)
                info.changeScoreBy(-3)
                timer.after(1000, function () {
                    INVISAFrames = false
                })
            }
        }
    } else if (KickTime == true) {
        sprites.destroy(otherSprite)
        mySprite.ay = -20000
        statusbar.value += 20
    } else if (mySprite.y > otherSprite.y) {
        if (INVISAFrames == false) {
            INVISAFrames = true
            info.changeLifeBy(-1)
            info.changeScoreBy(-3)
            timer.after(1000, function () {
                INVISAFrames = false
            })
        }
    }
})
