game.onUpdate(function () {
    if (GameStart == true) {
        if (Gravity == true) {
            if (NewOFF == false) {
                if (controller.B.isPressed()) {
                    if (characterAnimations.matchesRule(mySprite, characterAnimations.rule(Predicate.FacingRight))) {
                        mySprite.vx += 900000
                        if (characterAnimations.matchesRule(mySprite, characterAnimations.rule(Predicate.Moving))) {
                            statusbar.value += -3
                        }
                    } else if (characterAnimations.matchesRule(mySprite, characterAnimations.rule(Predicate.FacingLeft))) {
                        mySprite.vx += -900000
                        if (characterAnimations.matchesRule(mySprite, characterAnimations.rule(Predicate.Moving))) {
                            statusbar.value += -3
                        }
                    }
                    BOOST = true
                }
            }
        }
    }
    if (GameStart == true) {
        if (characterAnimations.matchesRule(mySprite, characterAnimations.rule(Predicate.NotMoving))) {
            BOOST = false
        }
        if (characterAnimations.matchesRule(mySprite, characterAnimations.rule(Predicate.HittingWallDown))) {
            if (StompTime == true) {
                characterAnimations.setCharacterAnimationsEnabled(mySprite, true)
                animation.stopAnimation(animation.AnimationTypes.All, mySprite)
                statusbar.setColor(10, 8)
                StompTime = false
                CoolDown2 = false
            }
        }
        if (GameStart == true) {
            if (mySprite.bottom >= scene.cameraProperty(CameraProperty.Bottom)) {
                GameOver()
            }
        }
    }
})
